import React, { Component } from 'react'

class ErrorHandlingComponent extends Component {
    constructor(props) {
      super(props)
    
      this.state = {
        hasError:false
      }
    }
    static getDerivedStateFromError(){
        this.setState({hasError:true})
    }

    render() {
        if(this.state.hasError)
            return <h1></h1>
        return (
            <div>
               {/* {this.props.name} */}
            </div>
        )
    }
}

export default ErrorHandlingComponent
