import 'bootstrap/dist/css/bootstrap.min.css';
import 'bootstrap/dist/js/bootstrap.bundle.min';
//import $ from 'jquery';
import React, { useState } from "react";

const Firstpage=(props)=>{
    const [count,setCount]=useState(0);
    const [val,setVal]=useState({name:'',dept:''});
    const [arr,setArr]=useState([]);
    const sty={
        top:"50%",
        left:'50%', 
        transform: "translate(-50%, -50%)",
        position:"absolute"
    }
    const assignCountBy10=(val)=>{
        setCount(
            (prevCount)=>{return prevCount+val}
        )
        setVal(
            (prevName)=>{
                alert(prevName);
                return prevName
            }
        )
    }

    /*let options = {
        method: 'POST',
        headers: {
            'Content-Type': 
            'application/json;charset=utf-8'
        },
        body: JSON.stringify({
            name:"hai",id:100
        })
    }
    fetch("https://reqres.in/api/users",options)
        .then(res =>res.json())
        .then(data=>console.log(data))
        .catch(error=>console.log("Error occured"));
    */   
    const a=[1,2,3,4,5]
    return(
        <div className="text-center" id="demo">
            <ul>
                {a.map((item,index)=>{return <li key={index}>{item}</li>})}
            </ul>

            <h4 className="text-success text-center">Firstpage!: {count}</h4>
            <button className="btn btn-success me-3" onClick={()=>setCount(count+1)}>Increment</button>
            <button className="btn btn-success me-3" onClick={()=>setCount(count-1)}>Decrement</button>
            <button className="btn btn-success" onClick={()=>assignCountBy10(10)}>Increment by 10</button>
            <br/>
            <p>{JSON.stringify(val)}</p>
            <form onSubmit={(event)=>{event.preventDefault();alert("Done!")}} style={sty}>
                <div className='form-floating'>
                    <input 
                        class='form-control' id='name' name='name'
                        type='text'
                        value={val.name}
                        onChange={(event)=>setVal({...val,name:event.target.value})}
                        placeholder=''
                        />
                    <label
                        className='' for='name'>Name:</label>
                </div>
                <div className='mt-3 form-floating'>
                    <input 
                        className='form-control' id='dept' name='dept'
                        value={val.dept}
                        onChange={(event)=>setVal({...val,dept:event.target.value})}
                        placeholder=''
                        />
                    <label
                        for='dept'>Dept:</label>
                </div>
                <input 
                    className='mt-3 btn btn-success'
                    type="submit"/>
            </form>
        </div>
    );
    
}
export default Firstpage;
