import React,{useState,useEffect} from 'react'

function FunctionalFormHandling() {
    const [name, setName]=useState( {firstName:'', lastName:''})
    const [arr,setArr] = useState([])
    const [count,setCount]=useState(0)
    const formSubmit=(e)=>{
        e.preventDefault()
    }
    const addValues=()=>{
        setArr([...arr,{index:arr.length,val:Math.floor(Math.random()*10)}])
    }
    const setcountVal=()=>{
        setCount((prevCount)=> prevCount+1)
        console.log(count)
    }
    useEffect(() => {
        window.addEventListener('click',setcountVal)
        console.log("useeffect");
        return()=>{
            window.removeEventListener('click',setcountVal)
        }
      
    },[])
    
    
    return (
        <div className='d-flex justify-content-center mt-5'>
            <form onSubmit={(e)=>formSubmit(e)} className='col-lg-4 col-md-7 col-sm-8 border px-4 py-3 was-validated' autoComplete='off'>
                <h4>{count}</h4>
                <div className='form-floating mb-3'>
                    <input className='form-control'
                        type="text"
                        value={name.firstName}
                        id='fname'
                        name='fname'
                        placeholder='Enter your first name'
                        onChange={(e)=>setName({...name,firstName:e.target.value})}
                        required
                    />
                    <label 
                        for="fname"
                    >First Name</label>
                    <div className='invalid-feedback'>please fill this required field</div>
                    <div className='valid-feedback'>you entered</div>
                </div>
                <div className='form-floating mb-3'>
                    <input className='form-control'
                        type="text"
                        id='lname'  
                        name='lname'
                        value={name.lastName}
                        placeholder='Enter your last name'
                        onChange={(e)=>setName({...name,lastName:e.target.value})}
                        required
                    />
                    <label
                        for="lname" 
                    >Last Name</label>
                    <div className='invalid-feedback'>please fill this required field</div>
                    <div className='valid-feedback'>you entered</div>
                </div>
                <div className='text-center'>
                    <button className='btn btn-outline-success px-5' type='submit' onClick={()=>addValues()}>Save</button>
                </div>
                <h5>{`${JSON.stringify(name)}`}</h5>
                <ol>
                    {arr.map((ele)=>{return <li key={ele.index}>{ele.val}</li>})}
                </ol>
            </form>   
        </div>
    )
}

export default FunctionalFormHandling
