            name:'rohit'
        }
        this.myRef=createRef()
        this.myRef=()=>{
            console.log("Hello Rohit!");
        }
    }
    componentDidMount() {
        this.myRef()
    }
    render() {
        return (
            <div className='text-center'>
                RefsClassComponent {this.m}
            </div>
        )
    }
}

