import React, { useCallback, useState } from 'react'
import UseCallbackB from './UseCallbackB'
import UseCallbackButton from './UseCallbackButton';

function UseCallbackA() {
  	const [age,setAge]=useState(21)
	const [salary,setSalary]=useState(1000000)
	console.log('parent component');
/*
	Use callback Hook prevents us from re-rendering child-components (optimize the child components) when one of the dependencies changed, in such case 
	we passes a method of a parent Component as prop to the child Component and put a dependency array to parent component method:)
*/
	const changeAge=useCallback(()=>{
		setAge(age+1)
	},[age])

	const changeSalary=useCallback(()=>{
		setSalary(salary+1)
	},[salary])

    return (
		<div>
			<UseCallbackB age={age}/>
			{/* <UseCallbackB age={salary}/> */}
			
			<UseCallbackButton className='btn btn-primary' txt='age' method={changeAge}/>
			<UseCallbackButton className='btn btn-primary' txt='salary' method={changeSalary}/>
		</div>
    )
}

export default UseCallbackA
