import React, { useCallback, useState } from 'react'
import UseCallbackButton from './UseCallbackButton'
import UseCallbackHookB from './UseCallbackHookB'

function UseCallbackHookA() {

    const [salary,setSalary]=useState(0)
    const [rollNo,setRollNo]=useState(0)

    // useCallback hook prevents us from re-rendering the child components when one of the dependencies changed :)
    // In useCallback, we just passing methods of the parent component to the child components
    const updateRollNo=useCallback(()=>{
        setRollNo(rollNo+1)
    },[rollNo])

    const updateSalaray=useCallback(()=>{
        setSalary(salary+10000)
    },[salary])

    return (
        <div>
            UseCallbackHookA
            <UseCallbackHookB val={salary}/>
            <UseCallbackHookB val={rollNo}/>
            <UseCallbackButton method={updateRollNo}></UseCallbackButton>
            <UseCallbackButton method={updateSalaray}></UseCallbackButton>
        </div>
    )
}

export default UseCallbackHookA
