import 'bootstrap/dist/css/bootstrap.min.css';
import 'bootstrap/dist/js/bootstrap.bundle.min';
//import $ from 'jquery';
import React, { useState } from "react";

const Firstpage=(props)=>{
    const [count,setCount]=useState(0);
    const [name,setName]=useState('');
    const sty={
        top:"50%",
        left:'50%', 
        transform: "translate(-50%, -50%)",
        position:"absolute"
    }
    const assignCount=(val)=>{
        setCount(
            (prevCount)=>{return prevCount+val}
        )
        setName(
            (prevName)=>{
                alert(prevName);
                return prevName
            }
        )
        let assignName=(e)=>{
            e.target
        }
    }
    return(
        <div className="text-center" id="demo">
            <h4 className="text-success text-center">Firstpage!: {count}</h4>
            <button className="btn btn-success" onClick={()=>setCount(count+1)}>Increment</button>
            <button className="btn btn-success" onClick={()=>setCount(count-1)}>Decrement</button>
            <button className="btn btn-success" onClick={()=>assignCount(10)}>Increment by 10</button>

            <form onSubmit={(event)=>{event.preventDefault();alert("Done!")}} style={sty}>
            NAME: <input 
                    type='text'
                    value={name}
                    onChange={( event)=>assignName}
                    ></input>
                    <input 
                        type="submit"></input>
            </form>
        </div>
    );
    
}
export default Firstpage;
