import React, { useMemo, useState } from 'react'

function UseMemoHook() {
    const [count1,setcount1]=useState(0)
    const [count2,setcount2]=useState(0)

    const updateText1=()=>{
        console.log('Rohit sharma');
        setcount1((prev)=>prev+1)
    }
    const updateText2=()=>{
        console.log('Virat Kohli');
        setcount2((prev)=>prev+1)
    }

    // the countValue function not re-rendered when any one of the state gets changed,
    // the updateText1 function called then the countValue function will be called :)
    const countValue=useMemo(()=>{
        let i=0
       while(i<10000000000)
            i++
        console.log('countValue function finished');
        return 
    },[count1])

    return (
        <div>
            {count1} - {count2}
            <button className='btn btn-primary' onClick={()=>updateText1()}>button1</button>
            <button className='btn btn-primary' onClick={()=>updateText2()}>button2</button>
            {countValue}
        </div>
    )
}

export default UseMemoHook
