import React from 'react'

function ContentDisplayError(props) {
    console.log('Content display');
    if(props.name==='Vishal')
    {
        // throws an error to the child component in ErrorBoundaryComponent :)
        throw new Error('Not a Cricketer')
    }
    return (
        <div>
            {props.name}
        </div>
    )
}

export default ContentDisplayError
