import 'bootstrap/dist/css/bootstrap.min.css';
import 'bootstrap/dist/js/bootstrap.bundle.min';
import React, { Component } from 'react'
import Firstpage from './Firstpage';
import Lifecycle from './Lifecycle';

class App extends Component {
  constructor(props)
  {
    super(props)

    this.state={
      count:0
    }
    console.log("in Constructor");
  }
  clickHandler=()=>{
    this.setState({
      count:this.state.count+1
    })
  }
  
  static getDerivedStateFromProps(props,state){
    console.log("in getDerivedStateFromProps");
    return null
  }
  render() {
    console.log("in render");
    return (
      <div className='container d-flex flex-row justify-content-between text-center' id="myid">
        <div>
          <h4 className='text-primary'>App!</h4>
          <button className='btn btn-primary' onClick={this.clickHandler}>{this.state.count}</button>
        </div>
        <Firstpage count={this.state.count}/>
        <Lifecycle/>
      </div>
    )
  }

  componentDidMount(){
    console.log("in componentDidMount");
    let ele=document.getElementById("demo") // child component(Life) id
    ele.classList.add("border")
  }
}

export default App;
