import React, { Component } from 'react'

/**
 * 1.Mounting 2. Updating 3. Unmounting
 * 
 * 1.Mounting:-
 *      -> constructor
 *      -> getDerivedStateFromProps
 *      -> Render 
 *      -> ComponentDidMount
 * 
 * 2. Updating:-
 *      -> getDerivedStateFromProps
 *      -> componentDidUpdate
 *      -> Render
 *      -> getSnapshotBeforeUpdate
 *      -> ShouldComponentUpdate
 * 
 * 3. Unmount:-
 *      -> willUnmount
 */
class LifeCycleComponent extends Component {

    // A constructor is called when the component is initialized :)
    constructor(props) {
        super(props)
        console.log('constructor called');
        this.state = {
            name:'Rohit'
        }
    }

    // the getDerivedStateFromProps method is called before rendering the elements into dom :)
    static getDerivedStateFromProps(props, state) {
        console.log('getDerivedStateFromProps');
        return null 
    }

    // componentDidMount method is called after child components and every elements are rendered into thw DOM :)
    componentDidMount() { 
        console.log('componentDidMount');
    }

    // shouldComponentUpdate method decides whether state should be updated or not :)
    shouldComponentUpdate(nextProps, nextState) { 
        console.log('shouldComponentUpdate');
        return true
    }

    
    getSnapshotBeforeUpdate = (prevProps, prevState) => {
        console.log('getSnapshotBeforeUpdate'+prevState.name);
        return null
    }

    componentDidUpdate(prevProps, prevState) {
        console.log('componentDidUpdate');
        return null
    }

    render() {
        console.log('render');
        return (
            <div>
                LifeCycleComponent {this.state.name}
                <button className='btn btn-primary' onClick={()=>this.setState({name:'Virat'})}>click</button>
            </div>
        )
    }
}

export default LifeCycleComponent
