import 'bootstrap/dist/css/bootstrap.min.css';
import 'bootstrap/dist/js/bootstrap.bundle.min';
import React, { Component } from 'react'
import Firstpage from './Firstpage';
import Lifecycle from './Lifecycle';

class App extends Component {
  constructor(props)
  {
    super(props)
    this.state={
      count:0
    }
    console.log("in Constructor");
  }

  clickHandler=()=>{
    this.setState({
      count:this.state.count+1
    })
  }
  
  static getDerivedStateFromProps(props,state){
    console.log("in getDerivedStateFromProps");
    return null
  }
  render() {
    console.log("in render");
    return (
      <div className='container d-flex flex-row justify-content-between text-center mt-5' id="myid">
        <div>
          <h4 className='text-primary'>App!</h4>
         
        </div>
        
      </div>
    )
  }

  componentDidMount(){
    console.log("in componentDidMount");
    let ele=document.getElementById("myid") // child component(Lifecycle.js) id
    ele.classList.add("border")
  }
}

export default App;
