import UseContextB from './UseContextB'

export const myContext = createContext('')

function UseContextA() {
    return (
        <>
            <UseContextA.Provider value={myContext}>
                <UseContextB />
            </UseContextA.Provider>
        </>
    )
}

export default UseContextA
