                <input 
                    type='text'
                    id='name'
                    name='name'
                    className='form-control'
                    value={userInp}
                    onChange={(e)=>setUserInp(e.target.value)}
                    placeholder=''
                />
                <label htmlFor='name'>Name</label>
            </div>   
            <inp.Provider value={userInp}>
                <useContextHookC/>
            </inp.Provider>
        </div>
    )
}

export default useContextHookA
