import React, { useReducer } from 'react'

// IntialState either a simple Value or an object:)
//const initialState=0

const initialState={
    count:0
}

function UseReducerComponent() {

    const reducer=(state,action)=>{

        switch(action.type)
        {
            case 'Increment':
                return {count:state.count+ac}
            case 'Decrement':
                return {count:state.count-1}
            case 'reset':
                return initialState
            default:
                return state.count
        }
    }

    // When the complex logic is needed to track the state of an application, we can use the useReducer Hook:)
    const [countVal,dispatch]=useReducer(reducer,initialState)

    return (
        <div className='container'>
            <div className='btn-group'>
                <button className='btn btn-primary' onClick={()=>dispatch({size:1,type:'Increment'})}>Increment</button>
                <button className='btn btn-primary' onClick={()=>dispatch({size:1,type:'Decrement'})}>Decrement</button>
                <button className='btn btn-primary' onClick={()=>dispatch({size:0,type:'reset'})}>Reset</button>
            </div><br/>
            {countVal.count}
        </div>
    )
}

export default UseReducerComponent
