import React, { useEffect, useState } from 'react'
//import FunctionB from './FunctionB';
import axios from 'axios'

function FunctionA() {
	const [titles,setTitles]=useState([])
	useEffect(()=>{
		fetchAPI();
	},[])

	const fetchAPI=()=>{
		const url='https://jsonplaceholder.typicode.com/todos';
		axios.get(url)
		.then((msg)=>{
			console.log(msg.data);
			setTitles(msg.data);
		})
		.catch((err)=>{
			console.log("I think something went wrong");
		})
	}

	const waitFunc=()=>{
		return new Promise((resolve,reject)=>{
			setTimeout(()=>{console.log("In setTimout");},3000);
		})
	}
	
	// async returns a promise:)
	async function asyncFunc(){ 
		console.log("first statement");
		// await wait for a promise:)
		await waitFunc();
		//console.log("after await waitFunc");
	}
	return (
		<>
			<div>
				{
					titles.map((val)=>{return <h5></h5>})
				}
			</div>
			<button className='btn btn-primary' onClick={asyncFunc}>Asynch function</button>
		</>
	)
}

export default FunctionA
