import React, { Component } from 'react'
import 'bootstrap/dist/css/bootstrap.min.css';
import 'bootstrap/dist/js/bootstrap.bundle.min';
import FunctionalFormHandling from './FunctionalFormHandling';
import axios from 'axios';

class FormHandling extends Component {
    // Mounting lifecycle:)
    constructor(props) {
        console.log("constructor called")
        super(props)
        
        this.state = {
            firstName:'',
            lastName:'',
            arr:[10,20,30,40,50,50],
            hide:0,
            index:0,
            key:[]
        }
        this.formSubmit.bind(this)
    }
    componentDidMount() { 
        console.log("component did mount method");
        
    }
    static getDerivedStateFromProps(props, state) {
        console.log("getDerivedStateFromProps method");
        return null
    }

    //UPDATING lifecycle:)
    shouldComponentUpdate(nextProps, nextState) {
        console.log('shouldComponent method')
        if(this.state.firstName==='')
            return false
        return true
    }

    componentDidUpdate(prevProps, prevState) {
        console.log('componentDidUpdate method'+prevState.firstName);
    }
    getSnapshotBeforeUpdate = (prevProps, prevState) => {
        console.log('getSnapshotBeforeUpdate method'+prevState.firstName);
    }

    formSubmit=(event)=>{
        event.preventDefault()
        console.log("form successfully submitted \n name: " +
        this.state.firstName+"\n lastName: " + this.state.lastName)
        axios.get('https://jsonplaceholder.typicode.com/todos/')
        .then((response)=>{
            this.setState({key:response.data})
        })
        .catch((err)=>{console.log(err)})
    }
    setFName(fname) {
        this.setState({firstName: name})    
    }
    setLName(name) {
        this.setState({lastName: name})
    }


    render() {
        console.log("render called");
        return (
            <div className='d-flex justify-content-center mt-5'>
                <ol>
                    {
                        this.state.key.map((resp)=>{return <li>{resp.title}</li>})
                    }
                </ol>
                <form onSubmit={this.formSubmit} className='col-lg-4 col-md-7 col-sm-8 border px-4 py-3 was-validated' autoComplete='off'>
                    <div className='form-floating mb-3'>

                        <input className='form-control'
                            type="text"
                            value={this.state.firstName}
                            id='fname'
                            name='fname'
                            placeholder='Enter your first name'
                            onChange={(e)=>this.setFName(e.target.value)}
                            required
                        />
                        <label 
                            for="fname"
                        >First Name</label>
                        <div className='invalid-feedback'>please fill this required field</div>
                        <div className='valid-feedback'>you entered</div>
                    </div>
                    <div className='form-floating mb-3'>
                        <input className='form-control'
                            type="text"
                            id='lname'
                            name='lname'
                            value={this.state.lastName}
                            placeholder='Enter your last name'
                            onChange={(e)=>this.setLName(e.target.value)}
                            required
                        />
                        <label
                            for="lname" 
                        >Last Name</label>
                        <div className='invalid-feedback'>please fill this required field</div>
                        <div className='valid-feedback'>you entered</div>
                    </div>
                    <div className='text-center'>
                        <button className='btn btn-outline-success px-5' type='submit'>Save</button>
                    </div>
                </form>
                <ol>
                    <button className='btn btn-primary' onClick={()=>{this.setState({hide:1})}}>click!</button>
                    {this.state.arr.map((ele,index)=>{return <li key={index}>{ele}</li>})}
                </ol>
                {/*this.state.hide===0 && <FunctionalFormHandling/>*/}
            </div>
        )
    }
}

export default FormHandling
