import 'bootstrap/dist/css/bootstrap.min.css';
import 'bootstrap/dist/js/bootstrap.bundle.min';
import React, { Component } from 'react'
//import Firstpage from './Firstpage';
//import Lifecycle from './Lifecycle';

 /*
 * Lifecycle of component
 * 1.Mouting =>{
 *      1.constructor()
 *      2.getDerivedStateFromProps(props,state)
 *      3.render()
 *      4.componentDidMount()
 *    }
 * 2.Updating =>{
 *      1.getDerivedStateFromProps()
 *      2.shouldComponentUpdate()
 *      3.render()
 *      4.getSnapshotBeforeUpdate(prevProps,prevState)
 *      5.componentDidUpdate()
 *  
 *    }
 * 3.unMounting =>{
 *      1.componentWillUnmount()
 *    }
 */

class App extends Component {
  constructor(props)
  {
    super(props)
    this.state={
      count:0,
      fav:'blue'
    }
    console.log("App in Constructor:"+this.state.fav);
  }

  // By default it always returns true.It checks whether the state should be change or not:)
  shouldComponentUpdate()
  {
    if(this.state.fav==='red' && this.state.count<=0)
      return false;
    else
      return true;
  }

  // it is Executed after the state or component gets updated:)
  componentDidUpdate() {
    console.log("cdu");
    alert("after fav is updated:"+this.state.fav);
  }

  clickHandler=()=>{
    this.setState({
      count:this.state.count+1,
      fav:'red'
    })
  }
  
  static getDerivedStateFromProps(props,state){
    console.log("in getDerivedStateFromProps");
    return {fav:'yellow'};
  }

  // it accepts two paramters i.e we will allow to access the prevState and prevProps when it gets updated:)
  getSnapshotBeforeUpdate = (prevProps, prevState) => {
    console.log(prevState.fav);
    return null;
  }

  render() {
    console.log("App in render");
    return (
      <div className='container d-flex flex-row justify-content-between text-center mt-5' id="myid">
        <div>
          <h4 className='text-primary'>App! -{this.state.fav}</h4>
          <button className='btn btn-primary' onClick={this.clickHandler}>{this.state.count}</button>
        </div>
        {/*<Firstpage count={this.state.count}/>*/}
        {/*<Lifecycle/>*/}
      </div>
    )
  }

  componentDidMount(){
    console.log("App in componentDidMount");
    let ele=document.getElementById("myid") // child component(Lifecycle.js) id
    ele.classList.add("border")
  }
}

export default App;
