/* eslint-disable jsx-a11y/anchor-is-valid */
import React, { useEffect } from 'react'

function SortListElements() {
    
    useEffect(()=>{
        
    },[])

    const sort=()=>{
        const ol=document.getElementById('myOl')
        const listItems=ol.querySelectorAll('li')
        let switchItems=false

        let i
        for(i=0;i<listItems.length-1;i++)
        {
            const prev=listItems[i].getElementsByTagName('a')[0]
            const prevlower=prev.textContent.toLowerCase()
            const curr=listItems[i+1].getElementsByTagName('a')[0]
            const currlower=curr.textContent.toLowerCase()

            if(prevlower>currlower){
                switchItems=true
                break
            }
        }
        if(switchItems)
            ol.insertBefore(listItems[i+1],listItems[i])
    }
    return (
        <div>
            <button onClick={sort} className='btn btn-primary mx-4 my-3'>click me</button>
            <ol className="mt-3" id="myOl">
                <li className="my-2 border rounnded rounded-5 py-2 ps-3 bg-light"><a href="" className="text-dark">Rohit sharma</a></li>
                <li className="my-2 border rounnded rounded-5 py-2 ps-3 bg-light"><a href="" className="text-dark">Shubman Gill</a></li>
                <li className="my-2 border rounnded rounded-5 py-2 ps-3 bg-light"><a href="" className="text-dark">Virat Kohli</a></li>
                <li className="my-2 border rounnded rounded-5 py-2 ps-3 bg-light"><a href="" className="text-dark">Shreyas Iyer</a></li>
                <li className="my-2 border rounnded rounded-5 py-2 ps-3 bg-light"><a href="" className="text-dark">Ishan Kishan</a></li>
                <li className="my-2 border rounnded rounded-5 py-2 ps-3 bg-light"><a href="" className="text-dark">Kl Rahul</a></li>
                <li className="my-2 border rounnded rounded-5 py-2 ps-3 bg-light"><a href="" className="text-dark">Ravindra Jadeja</a></li>
                <li className="my-2 border rounnded rounded-5 py-2 ps-3 bg-light"><a href="" className="text-dark">Hardik Pandya</a></li>
                <li className="my-2 border rounnded rounded-5 py-2 ps-3 bg-light"><a href="" className="text-dark">Jasprit Bumrah</a></li>
            </ol>
        </div>
    )
}

export default SortListElements
