/* eslint-disable jsx-a11y/anchor-is-valid */
import { useState,useEffect } from "react";

function FunctionB() {
	const [userInp,setUserInp]=useState('');

	useEffect(()=>{
		filterList()
	},[userInp])

	
	const filterList=()=>{
		const ol=document.getElementById("myOl")
		const upperInp=userInp.toUpperCase()
		const li=ol.getElementsByTagName("li")
		
		for(let i=0;i<li.length;i++){
			const liEle=li[i].getElementsByTagName("a")[0]
			const upper=liEle.innerText.toUpperCase()

			console.log(liEle.in);
			if(upper.indexOf(upperInp)>-1){
				li[i].style.display="block"
			}
			else
				li[i].style.display="none"


		}
	}

	return (
		<div className="container mt-5">

			<div className="form-floating">
				<input 
					name="uName"
					id="uName"
					className="w-50 form-control"
					type="text"
					value={userInp}
					placeholder="Enter your username"
					onChange={(e) =>setUserInp(e.target.value)}
				/>
				<label for="uName">Enter your username</label>
			</div>
			<ol className="mt-3" id="myOl" >
				<li><a href="" className="text-dark">Rohit sharma</a></li>
				<li><a href="" className="text-dark">Shubman Gill</a></li>
				<li><a href="" className="text-dark">Virat Kohli</a></li>
				<li><a href="" className="text-dark">Shreyas Iyer</a></li>
				<li><a href="" className="text-dark">Ishan Kishan</a></li>
				<li><a href="" className="text-dark">Kl Rahul</a></li>
				<li><a href="" className="text-dark">Ravindra Jadeja</a></li>
				<li><a href="" className="text-dark">Hardik Pandya</a></li>
				<li><a href="" className="text-dark">Axar Patel</a></li>
				<li><a href="" className="text-dark">Kuldeep Yadav</a></li>
				<li><a href="" className="text-dark">Jasprit Bumrah</a></li>
				<li><a href="" className="text-dark">Mohmmad Shami</a></li>
				<li><a href="" className="text-dark">Mohmmad Siraj</a></li>
			</ol>

		</div>
	)
}

export default FunctionB
