import React, { useReducer } from 'react'

// IntialState either a simple Value or an object:)
//const initialState=0

const initialState={
    incrementNumber:1
}

function UseReducerComponent() {

    const reducer=(state,action)=>{
        switch(action)
        {
            case 'Increment':
                return state+1
            case 'Decrement':
                return state-1
            case 'reset':
                return initialState
            default:
                return state
        }
    }

    // When the complex logic is needed to track the state of an application, we can use the useReducer Hook:)
    const [count,dispatch]=useReducer(reducer,initialState)

    return (
        <div className='container'>
            <div className='btn-group'>
                <button className='btn btn-primary' onClick={()=>dispatch('Increment')}>Increment</button>
                <button className='btn btn-primary' onClick={()=>dispatch('Decrement')}>Decrement</button>
                <button className='btn btn-primary' onClick={()=>dispatch('reset')}>Reset</button>
            </div><br/>
            {count}
        </div>
    )
}

export default UseReducerComponent
