import React,{useState,useEffect} from 'react'
import axios from 'axios'
import $ from 'jquery'

function FunctionalFormHandling() {
    const [name, setName]=useState( {firstName:'', lastName:''})
    const [arr,setArr] = useState([])
    const [count,setCount]=useState(0)
    const [apiData,setApiData] = useState([])

    const formSubmit=(e)=>{
        e.preventDefault()
    }
    const addValues=()=>{
        setArr([...arr,{index:arr.length,val:Math.floor(Math.random()*10)}])
    }
    const setcountVal=()=>{
        setCount((count)=> count+1)
        console.log(count)
    }
    useEffect(() => {
        window.addEventListener('click',setcountVal)
        console.log("useeffect")

        const userInp=document.getElementById('fname')
        fe(`https://jsonplaceholder.typicode.com/todos/${userInp.value}`)
        .then((response)=>{
            console.log(response.data)
            setApiData(response.data)
        })
        .catch((error)=>{console.log("Error")})

        // the implementation of component will unmount in useeffect:)
        return()=>{
            console.log('removeCount');
            window.removeEventListener('click',setcountVal)
        }
    },[name])
    
    
    return (
        <div className='ms-3'>
            <form onSubmit={(e)=>formSubmit(e)} className='col-lg-5 col-md-7 col-sm-8 border px-4 py-3 was-validated' autoComplete='off'>
                <h4>{count}</h4>
                <ol>
                    {<h4>{apiData.title}</h4>}
                    {/*apiData.map((e)=>{return <li key={e.id}>{e.title}</li>})*/}
                </ol>
                <div className='form-floating mb-3'>
                    <input className='form-control'
                        type="text"
                        value={name.firstName}
                        id='fname'
                        name='fname'
                        placeholder='Enter your first name'
                        onChange={(e)=>setName({...name,firstName:e.target.value})}
                        required
                    />
                    <label 
                        for="fname"
                    >First Name</label>
                    <div className='invalid-feedback'>please fill this required field</div>
                    <div className='valid-feedback'>you entered</div>
                </div>
                <div className='form-floating mb-3'>
                    <input className='form-control'
                        type="text"
                        id='lname'  
                        name='lname'
                        value={name.lastName}
                        placeholder='Enter your last name'
                        onChange={(e)=>setName({...name,lastName:e.target.value})}
                        required
                    />
                    <label
                        for="lname" 
                    >Last Name</label>
                    <div className='invalid-feedback'>please fill this required field</div>
                    <div className='valid-feedback'>you entered</div>
                </div>
                <div className='text-center'>
                    <button className='btn btn-outline-success px-5' type='submit' onClick={()=>addValues()}>Save</button>
                </div>
                <h5>{`${JSON.stringify(name)}`}</h5>
                <ol>
                    {arr.map((ele)=>{return <li key={ele.index}>{ele.val}</li>})}
                </ol>
            </form>   
        </div>
    )
}

export default FunctionalFormHandling
