import React, { useEffect, useState } from 'react'
//import $ from 'jquery'

function ToDoList() {
    const [userInp,setUserInp]=useState('') 
    const [listCount,setListCount]=useState(0)

    const ol=document.querySelector('#listItems')
    const removebtn=document.querySelectorAll('li button.btn-danger')

    const removeListItem=()=>{
        console.log("Remove List Item");
        removebtn.forEach((ele)=>{
            ele.addEventListener('click',()=>{
                //ol.removeChild(ele.parentNode)
                ele.parentNode.style.display = 'none'
                return
            })
        })
    }

    useEffect(()=>{

        removeListItem()

    },[listCount])

    const addItemFunc=()=>{
        if(userInp){
            const item=document.createElement("li")
            item.classList.add("list-group-item")
            item.classList.add("mt-2")
            item.textContent=userInp

            const remove=document.createElement("button")
            remove.classList.add("btn")
            remove.classList.add("btn-danger")
            remove.classList.add("float-end")
            remove.textContent = "Remove"

            item.appendChild(remove)
            
            ol.appendChild(item)
            
            setUserInp('')

            setListCount(listCount+1)
        }else{
            alert("Enter list item")
        }
    }
    const center={
        margin:"auto"
    }
    return (
        <div className='container'>
            <div className='col-lg-7 col-md-11 border rounded-4 mt-5' style={center}>
                <div className='d-flex flex-column justify-content-center mx-4 mb-3'>
                    
                        <form onSubmit={(e)=>{e.preventDefault();addItemFunc()}} style={{margin:"auto"}}>
                            <div className='form-floating'>
                                <input className='form-control'
                                    type='text'
                                    placeholder='Enter a list Item'
                                    value={userInp}
                                    name='items'
                                    id='items'
                                    onChange={(e)=>{setUserInp(e.target.value)}}
                                />
                                <label for="items" className=''>Enter a list Item</label>
                            </div>
                        </form>
                        <button className="btn btn-primary" id='addItem' onClick={addItemFunc}>Add Item</button>
                    
                    <ol className='list-group' id='listItems'>
                        
                    </ol>
                </div>
            </div>
        </div>
    )
}

export default ToDoList
