import React,{useRef} from 'react'
import { useEffect } from 'react'
import { useState } from 'react'

function UseRefHook() {
    const [userInp,setUserInp]=useState('')

    const myRef=useRef()

    useEffect(()=>{
        console.log('useEffect called');
        // useRef Hook prevents us from re-rendering 
        // myRef.current=
            // setInterval(()=>{
            //     console.log('setTimeout function called');
            // },1000)
            myRef.current=()=>{
                console.log('Function called using useRef Hook');
            }

        return ()=>{

        }
    })
    return (
        <div>
            UseRefHook
            <div className='form-floating'>
                <input className='form-control'
                    type='email'
                    placeholder=''
                    value={userInp}
                    onChange={(e)=>setUserInp(e.target.value)}
                />
                <label className='form-floating-label'>Enter Text</label>
            </div>
            <button className='btn btn-primary' onClick={()=>myRef.current()}>click me!</button>
        </div>
    )
}

export default UseRefHook
