import 'bootstrap/dist/css/bootstrap.min.css';
import 'bootstrap/dist/js/bootstrap.bundle.min';
//import $ from 'jquery';
import React, { useState } from "react";

const Firstpage=(props)=>{
    const [count,setCount]=useState(0);
    const [name,setName]=useState('');
    const sty={
        top:"50%",
        left:'50%', 
        transform: "translate(-50%, -50%)",
        position:"absolute"
    }
    const assignCountBy10=(val)=>{
        setCount(
            (prevCount)=>{return prevCount+val}
        )
        setName(
            (prevName)=>{
                alert(prevName);
                return prevName
            }
        )
    }
    console.log(fetch('http://api.example.com')).then((data)=> console.log(data))
    let fetchRes = fetch(
        "https://jsonplaceholder.typicode.com/todos/1");
        // fetchRes is the promise to resolve
        // it by using.then() method
        fetchRes.then(res =>
            res.json()).then(d => {
                console.log(d)
            })
    let assignName=(e)=>{
        setName(e.target.value);
    }
    return(
        <div className="text-center" id="demo">
            <h4 className="text-success text-center">Firstpage!: {count}</h4>
            <button className="btn btn-success" onClick={()=>setCount(count+1)}>Increment</button>
            <button className="btn btn-success" onClick={()=>setCount(count-1)}>Decrement</button>
            <button className="btn btn-success" onClick={()=>assignCountBy10(10)}>Increment by 10</button>

            <form onSubmit={(event)=>{event.preventDefault();alert("Done!")}} style={sty}>
            NAME: <input 
                    type='text'
                    value={name}
                    onChange={( event)=>assignName(event)}
                    ></input>
                    <input 
                        type="submit"></input>
            </form>
        </div>
    );
    
}
export default Firstpage;
