import 'bootstrap/dist/css/bootstrap.min.css';
import 'bootstrap/dist/js/bootstrap.bundle.min';
//import $ from 'jquery';
import React, { useState } from "react";

const Firstpage=(props)=>{
    const [count,setCount]=useState(0);
    const [val,setVal]=useState({name:'',dept:''});
    const sty={
        top:"50%",
        left:'50%', 
        transform: "translate(-50%, -50%)",
        position:"absolute"
    }
    const assignCountBy10=(val)=>{
        setCount(
            (prevCount)=>{return prevCount+val}
        )
        setVal(
            (prevName)=>{
                alert(prevName);
                return prevName
            }
        )
    }

    /*let options = {
        method: 'POST',
        headers: {
            'Content-Type': 
            'application/json;charset=utf-8'
        },
        body: JSON.stringify({
            name:"hai",id:100
        })
    }
    fetch("https://reqres.in/api/users",options)
        .then(res =>res.json())
        .then(data=>console.log(data))
        .catch(error=>console.log("Error occured"));
    */   

    let assignName=(e)=>{
        document.getElementById(val.name+"-"+val.dept);
        setVal(e.target.value);
    }
    return(
        <div className="text-center" id="demo">
            <h4 className="text-success text-center">Firstpage!: {count}</h4>
            <button className="btn btn-success" onClick={()=>setCount(count+1)}>Increment</button>
            <button className="btn btn-success" onClick={()=>setCount(count-1)}>Decrement</button>
            <button className="btn btn-success" onClick={()=>assignCountBy10(10)}>Increment by 10</button>
            <p id='content'></p>
            <form onSubmit={(event)=>{event.preventDefault();alert("Done!")}} style={sty}>
                <div className='form-floating'>

                    <input 
                        class='form-control' id='name' name='name'
                        type='text'
                        value={val.name}
                        onChange={(event)=>assignName(event)}
                        placeholder=''
                        />
                    <label
                        className='' for='name'>Name:</label>
                </div>
                <div className='form-floating'>
                    <input 
                        className='form-control' id='dept' name='dept'
                        value={val.dept}
                        onChange={(event)=>assignName(event)}
                        placeholder=''
                        />
                    <label
                        for='dept'>Dept:</label>
                </div>
                <input 
                    className='btn btn-success'
                    type="submit"/>
            </form>
        </div>
    );
    
}
export default Firstpage;
