import React, { useState } from 'react'

function ToDoList() {

    const [inp,setInp]=useState('')
    const [list,setList]=useState([])
    const [index,setIndex]=useState(0)

    const removeItem=()=>{

    }

    const addItems=()=>{
        if(inp!=='')
        {
            const newElement=<li className='list-group' key={index}>
                                {inp}
                                <a href='' className='btn btn-danger' onClick={()=>removeItem()}>Remove</a>
                            </li>

            setList([...list,newElement])

            setIndex(index+1)
        }
    }

    return (
        <div>
            ToDoList

            {/*********** User Inp ***********/}
            <div className='form-floating'>
                <input 
                    type='text'
                    placeholder=''
                    name='list'
                    id='list'
                    className='form-control'
                    value={inp}
                    onChange={(e)=>setInp(e.target.value)}
                />
            </div>
            <button className='btn btn-primary' onClick={()=>addItems()}>Add</button>

            {/*********** Display List Items ***********/}

                <ol>
                    {
                        list.map((item,index)=>
                            <>{item}</>
                        )
                    }
                </ol>

        </div>
    )
}

export default ToDoList
