import React, { useMemo, useState } from 'react'

function UseMemoComponent() {
    const [count1,setCount1]=useState(0)
    const [count2,setCount2]=useState(0)
    
    const Increment1=()=>{
        setCount1(count1+1)
    }
    const Increment2=()=>{
        setCount2(count2+1)
    }


    const isEven=useMemo(()=>{
        let i=0
        while(i<3000000000) 
            i++
        return count1%2===0
    })

    return (
        <div>

			<button className='btn btn-primary' onClick={Increment1}>button1 {count1}</button>

            {
                isEven()?'even':'odd'
            }

			<button className='btn btn-primary' onClick={Increment2}>button2 {count2}</button>
            
        </div>
    )
}

export default UseMemoComponent
