import React, { Component } from 'react'

class PureComponent extends Component {
    constructor(props) {
        super(props)
        
        this.state = {
            count:0
        }
    }

    update=() => {
        console.log('update');
        this.setState({count:0})
    }

    componentDidMount(){
        console.log('purecomponent is called');
    }

    render() {
        return (
            <div>
                PureComponent {this.state.count}
                <button className='btn btn-primary' onClick={this.update}>click</button>
            </div>

        )
    }
}

export default PureComponent
