import React, { useState } from 'react'

function PromisesFunc() {

    const [subscribed,setSubscribed]=useState(true)
    const [watched,setWatched]=useState(true)
    const [liked,setLiked]=useState(true)

    

    const start=()=>{

        // general promise method :)
        // const subscribe=new Promise((resolve,reject)=>{
        //     if(subscribed)
        //         resolve("user subscribed")
        //     else
        //         reject("user not subscribed")
        // })
        // subscribe.then((result)=>{
        //     console.log(result)
        // }).catch((err)=>console.log(err))

        const subscriber1=new Promise((resolve,reject)=>{
            if(subscribed)
            {
                resolve({
                    userId:701,
                    name:'Bharat',
                    desc:'Bharat subscribed'
                })
            }
            else if(watched)
            {
                resolve({
                    userId:701,
                    name:'Bharat',
                    desc:'Bharat watched successfully'
                })
            }
            else if(liked)
            {
                resolve({
                    userId:701,
                    name:'Bharat',
                    desc:'Bharat liked successfully'
                })
            }
            else
                reject("user not subscribed,watched and liked")
        })
        subscriber1.then((result)=>{
            console.log(result.userId+"\n"+result.name+"\n"+result.desc)
        }).catch((err)=>console.log(err))

        const subscriber2=new Promise((resolve, reject)=>{
            if(subscribed)
            {
                resolve({
                    userId:704,
                    name:'Mahesh',
                    desc:'Mahesh subscribed'
                })
            }
            else if(watched)
            {
                resolve({
                    userId:701,
                    name:'Mahesh',
                    desc:'Mahesh watched successfully'
                })
            }
            else if(liked)
            {
                resolve({
                    userId:701,
                    name:'Mahesh',
                    desc:'Bharat liked successfully'
                })
            }
            else
                reject("user not subscribed,watched and liked")
        })
    }

    return (
        <div className='text-center'>
            PromisesFunc
            <button className='btn btn-primary' onClick={()=>start()}>Subscribe</button>
        </div>
    )
}

export default PromisesFunc
