import React, { useState } from "react";
import UCUpdateCenturies from "./UCUpdateCenturies";
import UCUpdateButton from "./UCUpdateButton";

function UseCallbackHook()
{
    const [runs,setRuns]=useState(0)
    const [century,setCentury]=useState(0)

    const update_100=()=>{
        setCentury((century)=>century+1)
    }

    const updateRuns=()=>{
        setRuns((score)=>score+10) 
    }
    return(
        <div className="text-center">
            <UCUpdateButton method={update_100}/>
            <UCUpdateButton method={updateRuns}/>
            <UCUpdateCenturies runs={runs}/>
            <UCUpdateCenturies centuries/>
        </div>
    )
}

export default UseCallbackHook
