  }
  render() {
    return (
      <div style={{textAlign:"center"}}>

        {/*<button onClick={this.clickHandler.bind(this)}>click</button>*/}
        <button onClick={()=>this.clickHandler()}>{this.state.count}</button>
      </div>
    )
  }
}

export default App
