import React, { useReducer } from 'react'

const initialValue=0
function UseReducerHook() {

    const reducer=(current,action)=>{
        switch(action){
            case 45:    
                return 'Rohit'
            case 36:
                return 'Virat'
            case 7:
                return 'Dh'
            default:
                return 'choose crct option'
        }
    }

    const [text,dispatch]=useReducer(reducer,initialValue)


    return (
        <div >
            UseReducerHook - {text}
            <div className='btn-group'>
                <button type="button" class="btn btn-primary" onClick={()=>dispatch(45)}>button1</button>
                <button type="button" class="btn btn-primary" onClick={()=>dispatch(36)}>button2</button>
                <button type="button" class="btn btn-primary" onClick={()=>dispatch(7)}>button3</button>
            </div>
        </div>
    )
}

export default UseReducerHook
