import React, { useReducer } from 'react'

// const initialValue=''

const initialValue={
    name:'' // The Initial value might be an Object or a value :)
}
function UseReducerHook() {

    // const reducer=(current,action)=>{
    //     switch(action){
    //         case 45:    
    //             return 'Rohit'
    //         case 36:
    //             return 'Virat'
    //         case 7:
    //             return 'Dhoni'
    //         default:
    //             return 'choose crct option'
    //     }
    //}

    // the reducer function accepts two arguments such as the current value = initialValue and the action :)
    const reducer=(current,action)=>{
        
        switch(action){
            case 45:    
                return {name:'Rohit'} // returns an Object :)
            case 36:
                return {name:'Virat'}
            case 7:
                return {name:'Dhoni'}
            default:
                return 'choose crct option'
        }
    }

    /* useReducer accepts to parameters such as reducer and intialValue
     * the dispatch method indicates the action that we weill perform :)
    */
    const [text,dispatch]=useReducer(reducer,initialValue) 

    return (
        <div >
            UseReducerHook - {text.name}
            <div className='btn-group'>
                <button type="button" className="btn btn-primary" onClick={()=>dispatch(45)}>button1</button>
                <button type="button" className="btn btn-primary" onClick={()=>dispatch(36)}>button2</button>
                <button type="button" className="btn btn-primary" onClick={()=>dispatch(7)}>button3</button>
            </div>
        </div>
    )
}

export default UseReducerHook
