/* eslint-disable jsx-a11y/anchor-is-valid */
import React, { useState } from 'react'

function ToDoList() {

    const [inp,setInp]=useState('')
    const [list,setList]=useState([])
    const [index,setIndex]=useState(0)

    const removeItem=(removerIndex)=>{
        
        // setList(()=>
        //         list.filter((item)=> item.key!==removerIndex)
        // )
        console.log();

        setList((e)=>{
            return e.filter((eleIndex)=>{
                return eleIndex.key!==removerIndex
            })
        })
    }

    const addItems=()=>{
        if(inp!=='')
        {
            const newElement=<li className='list-group-item mt-2' key={index}>
                                <a href='#' className='text-decoration-none text-dark'>{inp}</a>
                                <button className='btn btn-danger float-end' onClick={()=>removeItem(index)}>Remove</button>
                            </li>

            setList([...list,newElement])

            setIndex(index+1)
        }
    }

    return (
        <div className='container col-6'>
            ToDoList

            {/*********** User Inp ***********/}
            <div className='input-group'>
                <div className='form-floating'>
                    <input 
                        type='text'
                        placeholder=''
                        name='list'
                        id='list'
                        className='form-control'
                        value={inp}
                        onChange={(e)=>setInp(e.target.value)}
                    />
                    <label className='form-label'>Enter Text...</label>
                </div>
                <button className='btn btn-primary px-4' onClick={()=>addItems()}>Add</button>
            </div>

            {/*********** Display List Items ***********/}

                <ol className='list-group'>
                    {
                        list.map((item,index)=>
                            item
                        )
                    }
                </ol>

        </div>
    )
}

export default ToDoList
