import React, { useReducer } from 'react'

const initial=0
function UseReducerHook() {

    const reducer=(val,action)=>{
        console.log(val)
        switch(action)
        {
            case 50:    return val+action
            case 100:   return val+action
            default: return val
        }
    }

    const [score,dispatch]=useReducer(reducer,initial)

    /**
     * 1. score =initial
     * 2. dispatch describes the action that we want to
     * 
     * 
     * 
    */
    return (
        <div className='text-center'>
            UseReducerHook {score}
            <button className='btn btn-primary' onClick={()=>dispatch(50)}>Increment1</button>
            <button className='btn btn-primary' onClick={()=>dispatch(100)}>Increment2</button>
        </div>
    )
}

export default UseReducerHook
