import React, { useEffect, useState,useRef } from 'react'

function UseRefComponent() {
    const [count,setCount]=useState()

    // Prevent the cause of Re-rendering a component multiple times:)
    // It allows us to call the function which is defined inside of useEffect:)
    const countVal=useRef(0)
    const myInterval=useRef()
    const valRef=useRef()

    useEffect(()=>{
        console.log("useEffect invoked");
        countVal.current=countVal.current + 1
        
        myInterval.current=setInterval(()=>{
            console.log('Hello Lucky!');
        },1000)

        valRef.current=()=>{
            console.log('inside useEffect');
            setCount(count+1)
        }
        
    },[])

    return (
        <div>
            UseRefComponent= {countVal.current}<br/>
            <input className='form-control w-50'
                type='text'
                value={count}
                onChange={()=>valRef.current()}
            />
            <button className='btn btn-danger' onClick={()=>clearInterval(myInterval.current)}>Clear</button>
        </div>
    )
}

export default UseRefComponent
