import React, { useState } from 'react'

function UseStateHook() {
    // form Handler with useState :)
    const [userInp,setUserInp]=useState({name:'',mail:''})

    // Array Implementation with useState :)
    const [myArray,setMyArray]=useState([])

    return (
        <div>
            <div className='container'>
                <form>
                    <div className='form-floating'>
                        <input className='form-control'
                            type='text'
                            placeholder=''
                            value={userInp.name}
                            onChange={(e)=>setUserInp({...userInp,name:e.target.value})}
                        />
                        <label className='form-floating-label'>Enter your name</label>
                    </div>
                    <div className='form-floating'>
                        <input className='form-control'
                            type='email'
                            placeholder=''
                            value={userInp.mail}
                            onChange={(e)=>setUserInp({...userInp,mail:e.target.value})}
                        />
                        <label className='form-floating-label'>Enter your Mail</label>
                    </div>
                </form>
                {JSON.stringify(userInp)}
                {
                    myArray.map((item,index)=>{return <h4 key={index}>{item}</h4>})
                }
            </div>
        </div>
    )
}

export default UseStateH
