import React, { useEffect, useState } from 'react'
//import FunctionB from './FunctionB'
import axios from 'axios'

function FunctionA() {
	const [userInp,setuserInp]=useState()
	const [titles,setTitles]=useState([])
	const [indexData,setIndexData]=useState([])
	const url=`http://20.244.56.144/numbers/primes`

	useEffect(()=>{
		//postAPI()
		fetchAPI()
	},[])

	const postAPI=()=>{
		return axios.put(`${url}/${1}`,{
			
		}).then(response=>{
			console.log(response.data);
		})
		.catch(err=>{
			console.log("something went wrong");
		})
	}

	const fetchAPI=()=>{

		axios.get(`${url}`)
		.then((msg)=>{
			console.log(msg.data.numbers)
			setTitles(msg.data.numbers)
			//setIndexData(msg.data.numbers)
		})
		.catch((err)=>{
			console.log("I think something went wrong")
		})
	}
	const setInpVal=(e)=>{
		setuserInp(e.target.value)
		fetchAPI(e.target.value)
	}
	

	const waitFunc=()=>{
		return new Promise((resolve,reject)=>{
			setTimeout(()=>{console.log("In setTimout")},3000)
		})
	}
	
	// async returns a promise:)
	async function asyncFunc(){ 
		console.log("first statement")
		// await wait for a promise:)
		await waitFunc()
		//console.log("after await waitFunc")
	}

	return (
		<>
			<div>
				{
					titles.map((data)=>{return <h6>{data}</h6>})
					//<h5>{indexData.title}</h5>
				}
			</div>
			<input 
				type='text'
				value={userInp}
				onChange={(e)=>setInpVal(e)}
			/>
			<button className='btn btn-primary' onClick={asyncFunc}>Asynch function</button>
		</>
	)
}

export default FunctionA
